#include "Platform_Types.h"
#define RCC_Base  0x40021000
#define Port_A  0x40010800
#define GPIOA_RCH    *(volatile uint32_t *)(Port_A+0x04)
#define GPIOA_ODR   *(volatile uint32_t *)(Port_A+0x0c)
#define RCC_APB2EN *(volatile uint32_t *)(RCC_Base+0x0c) //enable APB2 peripherals

int main(void)
{
	RCC_APB2EN|=1<<2; //enable clock for gpioA
	GPIOA_RCH &=0xff0fffff;
	GPIOA_RCH |=0x00200000;
	while(1){
		GPIOA_ODR |=1<<13;
		for(int i=0;i<5000;i++);
		GPIOA_ODR &=~1<<13;
		for(int i=0;i<5000;i++);
	}
	return 0;
}
